{  
   "sqlRequest":[  
      {  
         "requestId":"DeletePointData",
         "requestContent":"delete from point where uidpoint in (select po.uidpoint from pce p,grandeurs_objet gr,grandeur g,point po,qualite_point qpo where p.UIDGRANDEURS_OBJET=gr.UIDGRANDEURS_OBJET and gr.UIDGRANDEURS_OBJET=g.UIDGRANDEURS_OBJET and g.UIDGRANDEUR=po.UIDGRANDEUR and po.UIDQUALITE_POINT=qpo.UIDQUALITE_POINT and ((po.HORODATE >= TO_DATE('19/01/2017 06:00:00','DD/MM/YYYY HH24:MI:SS') and po.HORODATE <= TO_DATE('20/01/2017 05:00:00','DD/MM/YYYY HH24:MI:SS') )) and Idpce in ('METNEEPS1'))"
      },
      {  
         "requestId":"DeleteAcqPceData",
         "requestContent":"delete from acq_pce_conservee where uidacq_pce_conservee in (select uidacq_pce_conservee from acq_pce_conservee where code_acquisition in ('RCA_102') and jour_gazier= TO_DATE('19/01/2017 06:00:00','DD/MM/YYYY HH24:MI:SS'))"
      },
      {  
         "requestId":"DeletePubPointData",
         "requestContent":"delete from pub_point where uidgrandeur in (select pp.uidgrandeur from pub_point pp, pce p, grandeur g, GRANDEURS_OBJET go where  pp.uidgrandeur=g.UIDGRANDEUR and g.UIDGRANDEURS_OBJET=go.UIDGRANDEURS_OBJET and go.uidgrandeurs_objet=p.uidgrandeurs_objet and p.idpce='METNEEPS1' and horodate between TO_DATE('2017-01-19 06:00:00', 'YYYY-MM-DD HH24:MI:SS')and TO_DATE('2017-01-20 05:00:00', 'YYYY-MM-DD HH24:MI:SS'))"
      },
      {  
         "requestId":"DeleteEtrMeteringDataData",
         "requestContent":"delete from ETR_METERING_DATA  where  gas_day between TO_DATE('2017-01-19 06:00:00', 'YYYY-MM-DD HH24:MI:SS')and TO_DATE('2017-01-20 05:00:00', 'YYYY-MM-DD HH24:MI:SS') and insertion_date >= (sysdate-1/24/4) and pce_code like '%METNEEPS1%'"
      }
   ]
}

