{  
   "sqlRequest":[  
      {  
         "requestId":"CheckAlerte",
         "requestContent":"select count(*) as COUNT from alerte, fga where alerte.horodate>=(sysdate-3/24) and alerte.uidgrandeurs_objet=fga.uidgrandeurs_objet and IDfga='0709JLU10' and code_test='INIT' and alerte.lstime>=(sysdate-1/24) order by alerte.horodate desc, alerte.uidtest_ref desc,alerte.LSTIME DESC"
      }
           
   ]
}

{  
   "sqlRequest":[  
      {  
         "requestId":"CheckAlertePCEMJ",
         "requestContent":"select coalesce( (select count(*) as COUNT from alerte, pce,vpce,test_ref where alerte.uidgrandeurs_objet=pce.uidgrandeurs_objet and alerte.uidtest_ref=test_ref.uidtest_ref and pce.uidpce=vpce.uidpce AND vpce.DATE_FIN_AFFICHEE is null and code_test='INIT' and alerte.lstime>=(sysdate-1) and alerte.lstime<=sysdate and type_pce='M' and sous_type='J'),0) as count from dual"
      },
     {  
         "requestId":"CheckAlertePCEPoint",
         "requestContent":"select coalesce ((select count(*) COUNT from alerte, pce,vpce,test_ref where alerte.uidgrandeurs_objet=pce.uidgrandeurs_objet and alerte.uidtest_ref=test_ref.uidtest_ref and pce.uidpce=vpce.uidpce AND vpce.DATE_FIN_AFFICHEE is null and code_test='INIT' and source_acquisition in ('500','703') and alerte.lstime>=(sysdate-1) and alerte.lstime<=sysdate ),0) as count from dual"
      },     
	  {  
         "requestId":"CheckAlertePCESourceAcquisition",
         "requestContent":"select coalesce ((select count(*) COUNT from alerte, pce,vpce,test_ref where alerte.uidgrandeurs_objet=pce.uidgrandeurs_objet and alerte.uidtest_ref=test_ref.uidtest_ref and pce.uidpce=vpce.uidpce AND vpce.DATE_FIN_AFFICHEE is null and code_test='INIT' and type_point in ('T','8') and alerte.lstime>=(sysdate-1) and alerte.lstime<=sysdate),0) as count from dual"
      },   
  	  {  
         "requestId":"CheckAlertePCE",
         "requestContent":"select type_pce,sous_type, count(*) as count from alerte, pce,vpce,test_ref where alerte.uidgrandeurs_objet=pce.uidgrandeurs_objet and alerte.uidtest_ref=test_ref.uidtest_ref and pce.uidpce=vpce.uidpce AND vpce.DATE_FIN_AFFICHEE is null and code_test='INIT' and ( source_acquisition  not in ('T','8') or type_point in ('500','703') or (type_pce='M' and sous_type='J')) and alerte.lstime>=(sysdate-1) and alerte.lstime<=sysdate group by type_pce,sous_type"
      }
   ]
}
